import React, { useState } from 'react';

const BankForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    surname: '',
    phone: '',
    email: '',
    office: '',
    category: '',
    question: ''
  });

  const [errors, setErrors] = useState({});

  const offices = [
    'Glavni ured - Banja Luka',
    'Poslovnica Sarajevo',
    'Poslovnica Mostar',
    'Poslovnica Tuzla',
    'Poslovnica BihaÄ‡'
  ];

  const categories = [
    'TekuÄ‡i raÄun',
    'Å tednja',
    'Krediti',
    'Kartice',
    'Online banking',
    'Ostalo'
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Ime je obavezno';
    }

    if (!formData.surname.trim()) {
      newErrors.surname = 'Prezime je obavezno';
    }

    if (!formData.phone.trim()) {
      newErrors.phone = 'Broj telefona je obavezan';
    }

    if (!formData.office) {
      newErrors.office = 'Molimo odaberite poslovnicu';
    }

    if (!formData.category) {
      newErrors.category = 'Molimo odaberite kategoriju';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      alert(JSON.stringify(formData, null, 2));
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #f0f4ff 0%, #ffffff 100%)',
      padding: '40px 20px'
    }}>
      <div style={{
        maxWidth: '100%',
        margin: '0 auto',
        background: 'white',
        borderRadius: '12px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.07)',
        overflow: 'hidden'
      }}>
        {/* Header */}
        <div style={{
          background: 'linear-gradient(135deg, #e8f0fe 0%, #f0f7ff 100%)',
          padding: '32px',
          borderBottom: '2px solid #d4e3fc'
        }}>
          <h1 style={{
            margin: '0',
            fontSize: '28px',
            color: '#1a4d8f',
            fontWeight: '600',
            textAlign: 'center'
          }}>
            Kontakt forma
          </h1>
          <p style={{
            margin: '8px 0 0 0',
            fontSize: '15px',
            color: '#5a7ba6',
            textAlign: 'center'
          }}>
            Popunite formu i kontaktiraÄ‡emo vas u najkraÄ‡em moguÄ‡em roku
          </p>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} style={{ padding: '40px' }}>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
            gap: '24px',
            marginBottom: '24px'
          }}>
            {/* Name */}
            <div>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                fontSize: '14px',
