import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import BankForm from './BankForm';
import OperatorDashboard from './OperatorDashboard';
import NotificationPopup from './NotificationPopup';

function App() {
  return (
    <Router>
      <div>
        {/* Global notification popup - shows on all pages except dashboard */}
        <NotificationPopup />

        <Routes>
          <Route path="/zahtjevi" element={<BankForm />} />
          <Route path="/operator-dashboard" element={<OperatorDashboard />} />
          <Route path="/" element={
            <div style={{
              minHeight: '100vh',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              background: 'linear-gradient(135deg, #f0f4ff 0%, #ffffff 100%)'
            }}>
              <div style={{ textAlign: 'center' }}>
                <h1 style={{ color: '#1a4d8f', marginBottom: '20px' }}>Dobrodo≈°li</h1>
                <div style={{ display: 'flex', gap: '16px', justifyContent: 'center' }}>
                  <Link 
                    to="/zahtjevi" 
                    style={{
                      display: 'inline-block',
                      padding: '14px 32px',
                      fontSize: '16px',
                      fontWeight: '600',
                      color: 'white',
                      background: 'linear-gradient(135deg, #4a90e2 0%, #357abd 100%)',
                      textDecoration: 'none',
                      borderRadius: '8px',
                      boxShadow: '0 4px 12px rgba(74, 144, 226, 0.3)'
                    }}
                  >
                    Kontakt forma
                  </Link>
                  <Link 
                    to="/operator-dashboard" 
                    style={{
                      display: 'inline-block',
                      padding: '14px 32px',
                      fontSize: '16px',
                      fontWeight: '600',
                      color: 'white',
                      background: 'linear-gradient(135deg, #1a4d8f 0%, #2d6bb8 100%)',
                      textDecoration: 'none',
                      borderRadius: '8px',
                      boxShadow: '0 4px 12px rgba(26, 77, 143, 0.3)'
                    }}
                  >
                    Operator Dashboard
                  </Link>
                </div>
              </div>
            </div>
          } />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
