import React from 'react';

const QuestionModal = ({ question, onClose }) => {
  if (!question) return null;

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('sr-Latn-BA', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getTimeLeftColor = (minutes) => {
    if (minutes <= 0) return '#d32f2f';
    if (minutes <= 5) return '#f57c00';
    return '#4a90e2';
  };

  return (
    <div
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        zIndex: 10000,
        padding: '20px'
      }}
      onClick={onClose}
    >
      <div
        style={{
          background: 'white',
          borderRadius: '16px',
          maxWidth: '800px',
          width: '100%',
          maxHeight: '90vh',
          overflow: 'auto',
          boxShadow: '0 20px 60px rgba(0, 0, 0, 0.3)',
          animation: 'modalFadeIn 0.3s ease-out'
        }}
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header */}
        <div
          style={{
            background: 'linear-gradient(135deg, #1a4d8f 0%, #2d6bb8 100%)',
            color: 'white',
            padding: '24px 32px',
            borderRadius: '16px 16px 0 0',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}
        >
          <div>
            <h2 style={{ margin: '0', fontSize: '24px', fontWeight: '700' }}>
              Detalji pitanja
            </h2>
            <p style={{ margin: '4px 0 0 0', opacity: 0.9, fontSize: '14px' }}>
              ID: {question.id}
            </p>
          </div>
          <button
            onClick={onClose}
            style={{
              background: 'rgba(255, 255, 255, 0.2)',
              border: 'none',
              color: 'white',
              fontSize: '28px',
              width: '40px',
              height: '40px',
              borderRadius: '50%',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              transition: 'background 0.3s'
            }}
            onMouseEnter={(e) => e.target.style.background = 'rgba(255, 255, 255, 0.3)'}
            onMouseLeave={(e) => e.target.style.background = 'rgba(255, 255, 255, 0.2)'}
          >
            Ã—
          </button>
        </div>

        {/* Content */}
        <div style={{ padding: '32px' }}>
          {/* Status Banner */}
          <div
            style={{
              background: question.isHighAlert
                ? 'linear-gradient(135deg, #fff5f5 0%, #ffe8e8 100%)'
                : 'linear-gradient(135deg, #e8f0fe 0%, #f0f7ff 100%)',
              border: question.isHighAlert ? '2px solid #ffcdd2' : '2px solid #bbdefb',
              borderRadius: '12px',
              padding: '16px 20px',
              marginBottom: '24px',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center'
            }}
          >
            <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
              <span style={{ fontSize: '24px' }}>
                {question.isHighAlert ? 'ðŸš¨' : 'ðŸ“‹'}
              </span>
              <div>
                <div
                  style={{
                    fontSize: '16px',
                    fontWeight: '700',
                    color: question.isHighAlert ? '#d32f2f' : '#1a4d8f'
                  }}
                >
                  {question.isHighAlert ? 'Hitno pitanje' : 'Novo pitanje'}
                </div>
                <div style={{ fontSize: '13px', color: '#666', marginTop: '2px' }}>
                  {formatDate(question.submittedAt)}
                </div>
              </div>
            </div>
            <div
              style={{
                background: getTimeLeftColor(question.timeLeftMinutes),
                color: 'white',
                padding: '8px 16px',
                borderRadius: '20px',
                fontSize: '14px',
                fontWeight: '700'
              }}
            >
              {question.timeLeftMinutes > 0
                ? `${question.timeLeftMinutes} min preostalo`
                : 'Isteklo vrijeme'}
            </div>
          </div>

          {/* Customer Information */}
          <div style={{ marginBottom: '24px' }}>
            <h3
              style={{
                fontSize: '18px',
                fontWeight: '700',
                color: '#1a4d8f',
                marginBottom: '16px'
              }}
            >
              Informacije o klijentu
            </h3>
            <div
              style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
                gap: '16px'
              }}
            >
              <InfoField label="Ime i prezime" value={`${question.name} ${question.surname}`} />
              <InfoField label="Telefon" value={question.phone} icon="ðŸ“ž" />
              <InfoField
                label="Email"
                value={question.email || 'Nije dostupan'}
                icon="âœ‰ï¸"
              />
              <InfoField label="Poslovnica" value={question.office} icon="ðŸ¢" />
            </div>
          </div>

          {/* Category and Confidence */}
          <div style={{ marginBottom: '24px' }}>
            <h3
              style={{
                fontSize: '18px',
                fontWeight: '700',
                color: '#1a4d8f',
                marginBottom: '16px'
              }}
            >
              Kategorija i prepoznavanje
            </h3>
            <div style={{ display: 'flex', gap: '12px', flexWrap: 'wrap' }}>
              <Badge label="Kategorija" value={question.category} color="#4a90e2" />
              <Badge
                label="Tip"
                value={
                  question.matchType === 'exact'
                    ? 'TaÄno'
                    : question.matchType === 'similar'
                    ? 'SliÄno'
                    : 'Novo'
                }
                color="#9c27b0"
              />
              {question.confidence > 0 && (
                <Badge
                  label="Pouzdanost"
                  value={`${(question.confidence * 100).toFixed(0)}%`}
                  color="#43a047"
                />
