const QuestionCard = ({ question, onClick }) => {
  const [isHovered, setIsHovered] = useState(false);

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffMs = now - date;
    const diffMins = Math.floor(diffMs / 60000);

    if (diffMins < 60) return `Prije ${diffMins} min`;
    if (diffMins < 1440) return `Prije ${Math.floor(diffMins / 60)} h`;
    return date.toLocaleDateString('sr-Latn-BA');
  };

  const getTimeColor = (minutes) => {
    if (minutes <= 0) return '#d32f2f';
    if (minutes <= 5) return '#f57c00';
    return '#4a90e2';
  };

  return (
    <div
      onClick={onClick}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      style={{
        background: 'white',
        borderRadius: '10px',
        padding: '20px',
        marginBottom: '16px',
        border: '2px solid #e8f0fe',
        cursor: 'pointer',
        transition: 'all 0.3s ease',
        transform: isHovered ? 'translateY(-4px) scale(1.01)' : 'translateY(0) scale(1)',
        boxShadow: isHovered 
          ? '0 8px 24px rgba(74, 144, 226, 0.2)' 
          : '0 2px 8px rgba(0, 0, 0, 0.05)'
      }}
    >
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start', marginBottom: '12px' }}>
        <div>
          <div style={{
            display: 'inline-block',
            background: 'linear-gradient(135deg, #e8f0fe 0%, #f0f7ff 100%)',
            color: '#357abd',
            padding: '6px 14px',
            borderRadius: '6px',
            fontSize: '13px',
            fontWeight: '600',
            marginBottom: '8px'
          }}>
            {question.category}
          </div>
        </div>
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-end', gap: '4px' }}>
          <span style={{
            fontSize: '12px',
            color: '#5a7ba6',
            fontWeight: '500'
          }}>
            {formatTime(question.submittedAt)}
          </span>
          <span style={{
            fontSize: '12px',
            fontWeight: '700',
            color: getTimeColor(question.timeLeftMinutes),
            background: `${getTimeColor(question.timeLeftMinutes)}15`,
            padding: '4px 8px',
            borderRadius: '4px'
          }}>
            {question.timeLeftMinutes > 0 ? `${question.timeLeftMinutes} min` : 'Isteklo'}
          </span>
        </div>
      </div>

      <div style={{
        fontSize: '18px',
        fontWeight: '600',
        color: '#1a4d8f',
        marginBottom: '8px'
      }}>
        {question.name} {question.surname}
      </div>

      <div style={{
        fontSize: '14px',
        color: '#5a7ba6',
        lineHeight: '1.5',
        overflow: 'hidden',
        textOverflow: 'ellipsis',
        display: '-webkit-box',
        WebkitLineClamp: 2,
        WebkitBoxOrient: 'vertical'
      }}>
        {question.question}
      </div>

      <div style={{
        marginTop: '12px',
        paddingTop: '12px',
        borderTop: '1px solid #e8f0fe',
        display: 'flex',
        gap: '16px',
        fontSize: '12px',
        color: '#5a7ba6'
      }}>
        <span>üìû {question.phone}</span>
        <span>üè¢ {question.office}</span>
      </div>
    </div>
  );
};

