  const [selectedQuestion, setSelectedQuestion] = useState(null);

  // Fetch questions from backend
  const fetchQuestions = async () => {
    try {
      // Fetch high alert questions
      const highAlertRes = await fetch('http://localhost:5/api/questions/high-alert');
      const highAlertData = await highAlertRes.json();
      if (highAlertData.success) {
        setHighAlertQuestions(highAlertData.questions);
      }

      // Fetch new questions
