import React, { useState } from 'react';

const ConfirmationDialog = ({ message, onConfirm, onCancel }) => {
  return (
    <div
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.6)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        zIndex: 20000,
        padding: '20px'
      }}
      onClick={onCancel}
    >
      <div
        style={{
          background: 'white',
          borderRadius: '16px',
          padding: '32px',
          maxWidth: '400px',
          width: '100%',
          boxShadow: '0 20px 60px rgba(0, 0, 0, 0.3)',
          animation: 'modalFadeIn 0.2s ease-out'
        }}
        onClick={(e) => e.stopPropagation()}
      >
        <h3 style={{
          margin: '0 0 16px 0',
          fontSize: '20px',
          fontWeight: '700',
          color: '#1a4d8f'
        }}>
          Potvrda
        </h3>
        <p style={{
          margin: '0 0 24px 0',
          fontSize: '15px',
          lineHeight: '1.5',
          color: '#333'
        }}>
          {message}
        </p>
        <div style={{ display: 'flex', gap: '12px', justifyContent: 'flex-end' }}>
          <button
            onClick={onCancel}
            style={{
              padding: '10px 24px',
              fontSize: '14px',
              fontWeight: '600',
              color: '#666',
              background: '#f5f5f5',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer',
              transition: 'all 0.2s'
            }}
            onMouseEnter={(e) => e.target.style.background = '#e0e0e0'}
            onMouseLeave={(e) => e.target.style.background = '#f5f5f5'}
          >
            Otka≈æi
          </button>
          <button
            onClick={onConfirm}
            style={{
              padding: '10px 24px',
              fontSize: '14px',
              fontWeight: '600',
              color: 'white',
              background: 'linear-gradient(135deg, #4a90e2 0%, #357abd 100%)',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer',
              transition: 'all 0.2s',
              boxShadow: '0 2px 8px rgba(74, 144, 226, 0.3)'
            }}
            onMouseEnter={(e) => {
              e.target.style.transform = 'translateY(-1px)';
              e.target.style.boxShadow = '0 4px 12px rgba(74, 144, 226, 0.4)';
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 2px 8px rgba(74, 144, 226, 0.3)';
            }}
          >
            Potvrdi
          </button>
        </div>
      </div>
    </div>
  );
};

const QuestionModal = ({ question, onClose, onQuestionAnswered }) => {
  const [operatorAnswer, setOperatorAnswer] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [markedAsAnswered, setMarkedAsAnswered] = useState(false);

  if (!question) return null;

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('sr-Latn-BA', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getTimeLeftColor = (minutes) => {
    if (minutes <= 0) return '#d32f2f';
    if (minutes <= 5) return '#f57c00';
    return '#4a90e2';
  };

  const handleMarkAsAnswered = () => {
    setShowConfirmation(true);
  };

  const handleConfirmMarkAsAnswered = async () => {
    setIsSubmitting(true);
    setShowConfirmation(false);

    try {
      const response = await fetch(`http://localhost:5235/api/questions/${question.id}/mark-answered`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          operatorAnswer: operatorAnswer || 'Pitanje je odgovoreno telefonskim putem.'
        })
      });

      const result = await response.json();

      if (result.success) {
        setMarkedAsAnswered(true);

        // Show success message
        setTimeout(() => {
          if (onQuestionAnswered) {
            onQuestionAnswered(question.id);
          }
          onClose();
        }, 1500);
      } else {
        alert('Gre≈°ka: ' + (result.error || 'Nepoznata gre≈°ka'));
      }
    } catch (error) {
      console.error('Error marking as answered:', error);
      alert('Gre≈°ka pri a≈æuriranju pitanja. Molimo poku≈°ajte ponovo.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <div
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 10000,
          padding: '20px'
        }}
        onClick={onClose}
      >
        <div
          style={{
            background: 'white',
            borderRadius: '16px',
            maxWidth: '800px',
            width: '100%',
            maxHeight: '90vh',
            overflow: 'auto',
            boxShadow: '0 20px 60px rgba(0, 0, 0, 0.3)',
            animation: 'modalFadeIn 0.3s ease-out'
          }}
          onClick={(e) => e.stopPropagation()}
        >
          {/* Success Overlay */}
          {markedAsAnswered && (
            <div style={{
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              background: 'rgba(255, 255, 255, 0.95)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              zIndex: 100,
              borderRadius: '16px'
            }}>
              <div style={{ textAlign: 'center' }}>
                <div style={{ fontSize: '64px', marginBottom: '16px' }}>‚úì</div>
                <div style={{ fontSize: '24px', fontWeight: '700', color: '#43a047' }}>
                  Pitanje je oznaƒçeno kao odgovoreno!
                </div>
              </div>
            </div>
          )}

          {/* Header */}
          <div
            style={{
              background: 'linear-gradient(135deg, #1a4d8f 0%, #2d6bb8 100%)',
              color: 'white',
              padding: '24px 32px',
              borderRadius: '16px 16px 0 0',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center'
            }}
          >
            <div>
              <h2 style={{ margin: '0', fontSize: '24px', fontWeight: '700' }}>
                Detalji pitanja
              </h2>
              <p style={{ margin: '4px 0 0 0', opacity: 0.9, fontSize: '14px' }}>
                ID: {question.id}
              </p>
            </div>
            <button
              onClick={onClose}
              style={{
                background: 'rgba(255, 255, 255, 0.2)',
                border: 'none',
                color: 'white',
                fontSize: '28px',
                width: '40px',
                height: '40px',
                borderRadius: '50%',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                transition: 'background 0.3s'
              }}
              onMouseEnter={(e) => e.target.style.background = 'rgba(255, 255, 255, 0.3)'}
              onMouseLeave={(e) => e.target.style.background = 'rgba(255, 255, 255, 0.2)'}
            >
              √ó
            </button>
          </div>

          {/* Content */}
          <div style={{ padding: '32px' }}>
            {/* Status Banner */}
            <div
              style={{
                background: question.isHighAlert
                  ? 'linear-gradient(135deg, #fff5f5 0%, #ffe8e8 100%)'
                  : 'linear-gradient(135deg, #e8f0fe 0%, #f0f7ff 100%)',
                border: question.isHighAlert ? '2px solid #ffcdd2' : '2px solid #bbdefb',
                borderRadius: '12px',
                padding: '16px 20px',
                marginBottom: '24px',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
              }}
            >
              <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                <span style={{ fontSize: '24px' }}>
                  {question.isHighAlert ? 'üö®' : 'üìã'}
                </span>
                <div>
                  <div
                    style={{
                      fontSize: '16px',
                      fontWeight: '700',
                      color: question.isHighAlert ? '#d32f2f' : '#1a4d8f'
                    }}
                  >
                    {question.isHighAlert ? 'Hitno pitanje' : 'Novo pitanje'}
                  </div>
                  <div style={{ fontSize: '13px', color: '#666', marginTop: '2px' }}>
                    {formatDate(question.submittedAt)}
                  </div>
                </div>
              </div>
              <div
                style={{
                  background: getTimeLeftColor(question.timeLeftMinutes),
                  color: 'white',
                  padding: '8px 16px',
                  borderRadius: '20px',
                  fontSize: '14px',
                  fontWeight: '700'
                }}
              >
                {question.timeLeftMinutes > 0
                  ? `${question.timeLeftMinutes} min preostalo`
                  : 'Isteklo vrijeme'}
              </div>
            </div>

            {/* Customer Information */}
            <div style={{ marginBottom: '24px' }}>
              <h3
                style={{
                  fontSize: '18px',
                  fontWeight: '700',
                  color: '#1a4d8f',
                  marginBottom: '16px'
                }}
              >
                Informacije o klijentu
              </h3>
              <div
                style={{
                  display: 'grid',
                  gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
                  gap: '16px'
                }}
              >
                <InfoField label="Ime i prezime" value={`${question.name} ${question.surname}`} />
                <InfoField label="Telefon" value={question.phone} icon="üìû" />
                <InfoField
                  label="Email"
                  value={question.email || 'Nije dostupan'}
                  icon="‚úâÔ∏è"
                />
                <InfoField label="Poslovnica" value={question.office} icon="üè¢" />
              </div>
            </div>

            {/* Category and Confidence */}
            <div style={{ marginBottom: '24px' }}>
              <h3
                style={{
                  fontSize: '18px',
                  fontWeight: '700',
                  color: '#1a4d8f',
                  marginBottom: '16px'
                }}
              >
                Kategorija pitanja
              </h3>
              <div style={{ display: 'flex', gap: '12px', flexWrap: 'wrap' }}>
                <Badge label="Kategorija" value={question.category} color="#4a90e2" />
              </div>
            </div>

            {/* Question */}
            <div style={{ marginBottom: '24px' }}>
              <h3
                style={{
                  fontSize: '18px',
                  fontWeight: '700',
                  color: '#1a4d8f',
                  marginBottom: '12px'
                }}
              >
                Pitanje
              </h3>
              <div
                style={{
                  background: '#f8fafb',
                  padding: '20px',
                  borderRadius: '12px',
                  border: '2px solid #e8f0fe',
                  fontSize: '15px',
                  lineHeight: '1.6',
                  color: '#333'
                }}
              >
                {question.question}
              </div>
            </div>

            {/* Suggested Answer */}
            {question.suggestedAnswer && (
              <div style={{ marginBottom: '24px' }}>
                <h3
                  style={{
                    fontSize: '18px',
                    fontWeight: '700',
                    color: '#1a4d8f',
                    marginBottom: '12px'
                  }}
                >
                  Dostavljeni odgovor (AI)
                </h3>
                <div
                  style={{
                    background: 'linear-gradient(135deg, #e8f0fe 0%, #f0f7ff 100%)',
                    padding: '20px',
                    borderRadius: '12px',
                    border: '2px solid #bbdefb',
                    fontSize: '15px',
                    lineHeight: '1.6',
                    color: '#333'
                  }}
                >
                  {question.suggestedAnswer}
                </div>
              </div>
            )}

            {/* Answer Section */}
            <div style={{ marginBottom: '24px' }}>
              <h3
                style={{
                  fontSize: '18px',
                  fontWeight: '700',
                  color: '#1a4d8f',
                  marginBottom: '12px'
                }}
              >
                Odgovor
              </h3>
              <textarea
                value={operatorAnswer}
                onChange={(e) => setOperatorAnswer(e.target.value)}
                placeholder="Opciono: Unesite odgovor..."
                disabled={isSubmitting}
                style={{
                  width: '100%',
                  minHeight: '120px',
                  padding: '16px',
                  fontSize: '15px',
                  border: '2px solid #d4e3fc',
                  borderRadius: '12px',
                  resize: 'vertical',
                  fontFamily: 'inherit',
                  background: 'white',
                  boxSizing: 'border-box',
                  opacity: isSubmitting ? 0.6 : 1
                }}
                onFocus={(e) => e.target.style.borderColor = '#4a90e2'}
                onBlur={(e) => e.target.style.borderColor = '#d4e3fc'}
              />

              {/* Instructions */}
              <div style={{
                marginTop: '12px',
                padding: '12px 16px',
                background: '#fff8e1',
                border: '1px solid #ffecb3',
                borderRadius: '8px',
                fontSize: '13px',
                color: '#666'
              }}>
                <strong>üìû Uputstvo:</strong> Nazovite klijenta na {question.phone}, odgovorite na pitanje, 
                a zatim oznaƒçite kao odgovoreno klikom na dugme ispod.
              </div>

              <div style={{ marginTop: '16px', textAlign: 'right' }}>
                <button
                  onClick={handleMarkAsAnswered}
                  disabled={isSubmitting}
                  style={{
                    padding: '14px 32px',
                    fontSize: '15px',
                    fontWeight: '600',
                    color: 'white',
                    background: isSubmitting 
                      ? '#ccc' 
                      : 'linear-gradient(135deg, #43a047 0%, #2e7d32 100%)',
                    border: 'none',
                    borderRadius: '8px',
                    cursor: isSubmitting ? 'not-allowed' : 'pointer',
                    transition: 'all 0.3s',
                    boxShadow: '0 4px 12px rgba(67, 160, 71, 0.3)'
                  }}
                  onMouseEnter={(e) => {
                    if (!isSubmitting) {
                      e.target.style.transform = 'translateY(-2px)';
                      e.target.style.boxShadow = '0 6px 16px rgba(67, 160, 71, 0.4)';
                    }
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.transform = 'translateY(0)';
                    e.target.style.boxShadow = '0 4px 12px rgba(67, 160, 71, 0.3)';
                  }}
                >
                  {isSubmitting ? 'ƒåuvam...' : '‚úì Oznaƒçi kao odgovoreno'}
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Animation styles */}
        <style>
          {`
            @keyframes modalFadeIn {
              from {
                opacity: 0;
                transform: scale(0.9);
              }
              to {
                opacity: 1;
                transform: scale(1);
              }
            }
          `}
        </style>
      </div>

      {/* Confirmation Dialog */}
      {showConfirmation && (
        <ConfirmationDialog
          message="Da li ste sigurni da ≈æelite oznaƒçiti ovo pitanje kao odgovoreno? Ova akcija ƒáe ukloniti pitanje iz liste nerije≈°enih pitanja."
          onConfirm={handleConfirmMarkAsAnswered}
          onCancel={() => setShowConfirmation(false)}
        />
      )}
    </>
  );
};

const InfoField = ({ label, value, icon }) => (
  <div>
    <div style={{ fontSize: '12px', color: '#666', marginBottom: '4px', fontWeight: '500' }}>
      {label}
    </div>
    <div style={{ fontSize: '15px', color: '#333', fontWeight: '600' }}>
      {icon && <span style={{ marginRight: '6px' }}>{icon}</span>}
      {value}
    </div>
  </div>
);

const Badge = ({ label, value, color }) => (
  <div
    style={{
      display: 'inline-flex',
      alignItems: 'center',
      gap: '8px',
      background: `${color}15`,
      border: `2px solid ${color}40`,
      padding: '8px 16px',
      borderRadius: '20px'
    }}
  >
    <span style={{ fontSize: '12px', color: '#666', fontWeight: '500' }}>{label}:</span>
    <span style={{ fontSize: '14px', color: color, fontWeight: '700' }}>{value}</span>
  </div>
);

export default QuestionModal;
